INSERT INTO `makgeolli` VALUES ('1', 'Jipyeong Fresh Makgeolli', '2', 'Makgeolli, registered as a registered cultural property in Korea, is made from domestic rice and clear groundwater as raw materials, and it is brewed in the same way as the traditional method, so it is smooth and has a good taste of water. It is characterized by a slightly sweet taste.', 'Korea', 'traditional', 'smooth', 'sweet', '4.8', 'https://image.ajunews.com/content/image/2019/04/02/20190402100221456599.jpg')
INSERT INTO `makgeolli` VALUES ('2', 'Incheon Soseongju', '6', 'It is Incheon\'s representative makgeolli. The makgeolli\'s peculiar taste is reduced and it is refreshing by adding a fresh feeling. It features 12 zodiac animals on the makgeolli.', 'Korea', 'Incheon', 'fresh', '12zodiac', '4.7', 'https://lh3.googleusercontent.com/proxy/Kt3XQ2CtEiZ3E4Ub_2azlZz0xuirdmQ59y9CMA9ehdhV-JvE_gc4tuHiomcNFMHH9dS3kvmRsubOwr2Ba1X6POeEbA')
INSERT INTO `makgeolli` VALUES ('3', 'Gongju Bamdongsan Bammakgeolli', '7', 'It is one of the makgeolli made with chestnut, a special product of Gongju. The dark yellow chestnut makgeolli is thick with the addition of steamed chestnuts. It is sweet and rich in flavour. ', 'Korea', 'Gongju', 'chesnut', 'sweet', '4.5', 'https://i0.wp.com/www.sulsulsul.com/wp-content/uploads/2020/06/%EA%B3%B5%EC%A3%BC-%EB%B0%A4%EB%8F%99%EC%82%B0-%EC%95%8C%EB%B0%A4-%EB%A7%89%EA%B1%B8%EB%A6%AC.jpg?fit=1000%2C1000&ssl=1')
INSERT INTO `makgeolli` VALUES ('4', 'Gurye Shandong Sansuyu Makgeolli', '8', 'It is makgeolli produced with cornus oil grown in the village of Shandong, Gurye-gun, Jirisan. It has a light strawberry milk color and a light and refreshing taste. The scent of cornus is well felt.', 'Korea', 'Gurye', 'cornus', 'light', '4.3', 'https://lh3.googleusercontent.com/proxy/A_Wn2EkE47UjmDCPUhRtB6kg237xevF49EOEMzlA00lI1aNFCjYKdCVjz6rg1rnP7Nmv9QWQnm1nQPxNKmLMMShs5aKZYT6gfSoMGe890MR_WfIAig')
INSERT INTO `makgeolli` VALUES ('5', 'Udo Peanut Makgeolli', '9', 'It is a makgeolli made with peanuts, a special product of Udo. Makgeolli, which contains the rich flavor and aroma of peanuts, has a sweet and rich taste.', 'Korea', 'Udo', 'peanut', 'sweet', '4.1', 'https://goodjirisan.com/web/product/big/201712/56_shop1_159413.jpg')
INSERT INTO `makgeolli` VALUES ('6', 'Jeongseon Auraji Corn Makgeolli', '10', 'It is a traditional liquor brewed in the traditional way using pure groundwater and corn from Jeongseon. At the end, you can feel the fragrant aroma and taste of corn, and it is characterized by a higher degree than other makgeolli.', 'Korea', 'corn', 'Jeoungseon', 'high', '4.6', 'https://img.daily.co.kr/@files/www.daily.co.kr/content/food/2017/20170411/66e7aab5d82733718ac8752a2a5592f9.png')
INSERT INTO `makgeolli` VALUES ('7', 'Pocheon idong maggeolli', '11', 'Makgeolli is made from pure mineral water in Baekdong Valley, so it has a unique and subdued taste unlike other makgeolli. ', 'Korea', 'Pocheon', 'mineral', 'unique', '4.7', 'https://lh3.googleusercontent.com/proxy/x4ujwkfaYXKDazuiSERq0vUPgf11WAIujjmnWU8xL3T1C86vadDX_nv8qujbX8eLVujO-FvQ9du_Sd2-5rBfghkCB6VTyPoCVW4jD4U4Pu1V2PPdtQWMS2qiYGQir4OVQ5b4QyA')
INSERT INTO `makgeolli` VALUES ('8', 'Baedari Makgeolli', '12', 'It is made using organic rice produced in Goyang, Gyeonggi-do. It is a makgeolli that is so soft that it is not carbonated, and it idoes not have any problem even if you shake it sufficiently.', 'Korea', 'Goyang', 'soft', 'refreshing', '4.1', 'https://lh3.googleusercontent.com/proxy/tr7usC0PhsgOxovJGv7CBgLfexFkwsYvt--u41A2leb93_CYTvnssQYLj8Z5xZ7SQVpl3KcskFRIQdxLEeG3T7IR')
