INSERT INTO `makgeolli` VALUES ('10', 'Wonju Chiaksan Makgeolli', '13', 'It is a fresh makgeolli, which is characterized by its umami and refreshing taste that combines lightness and flesh.\r\nIt is made from water extracted from 100m underground and has a unique refreshing feeling.', 'Korea', 'Wonju', 'refreshing', 'light', '3.7', 'https://mblogthumb-phinf.pstatic.net/MjAxODExMDZfNDgg/MDAxNTQxNDgxOTEzNDkz.VLwciLYz9XeHttZFGW5MWWCFCrYLCyRJD5PM3_f283sg.S7uIAIT9JP-JwUq1ioHeTlbWMx8lsn0Rc5BgqF6MC2cg.PNG.taekdaeri/%EC%B9%98%EC%95%85%EC%82%B0_%EB%A7%89%EA%B1%B0%EB%A6%AC.png?type=w800')
