INSERT INTO `makgeolli` VALUES ('13', 'Guksundang Rice Makgeolli', '3', 'Makgeolli made with 100% Korean rice, and it is soft and clean.', 'Korea', 'rice', 'clean', 'soft', '3.4', 'https://k-shop.eu/2455-large_default/%EA%B5%AD%EC%88%9C%EB%8B%B9-%EB%A7%89%EA%B1%B8%EB%A6%AC-6%EB%8F%84-750ml.jpg')
INSERT INTO `makgeolli` VALUES ('14', 'Maksai Makgeolli', '15', 'It is a collaboration between light raw makgeolli and refreshing cider. The attractiveness is the sweet refreshing sensation that captures the thick taste of makgeolli. It is raw makgeolli made of rich natural carbonic acid.', 'Korea', 'cider', 'sweet', 'refreshing', '3.5', 'http://www.busan.com/nas/data/content/image/2016/03/15/20160315000126_0.jpg')
INSERT INTO `makgeolli` VALUES ('15', 'Gapyeong Pine Nut Makgeolli', '4', 'It is a makgeolli made with pine nuts, a special product of Gapyeong, and the scent of pine nuts goes well with the solid base of makgeolli.It is the Blue House official dinner liquor.', 'Korea', 'Gapyeong', 'pinenut', 'subdued', '3.4', 'https://image.ajunews.com/content/image/2020/06/19/20200619105317216819.jpg')
INSERT INTO `makgeolli` VALUES ('16', 'Neulinma-eul Maggeolli ', '16', 'The rice content of makgeolli is two or three times that of other makgeolli. It does not contain artificial sweeteners, has strong banana and yogurt scents, and is smooth.', 'Korea', 'rice', 'smooth', 'healthy', '3.7', 'https://www.wikileaks-kr.org/news/photo/201903/51205_28478_4043.jpg')
INSERT INTO `makgeolli` VALUES ('17', '100 billion Lactobacillus Makgeolli', '17', 'It contains 1000 times more Lactobacillus than raw makgeolli, and 100 times more Lactobacillus than Lactobacillus drink. It is less dry, clean, and smooth. ', 'Korea', 'Lactobacillus', 'clean', 'smooth', '3.1', 'https://cdn.lcnews.co.kr/news/photo/202004/6177_7005_1534.jpg')
INSERT INTO `makgeolli` VALUES ('18', 'Life makgeolli', '18', 'It is makgeolli made aimed at young people. It is sweet, fresh, and strong in carbonate.', 'Korea', 'sweet', 'fresh', 'carbonated', '2.8', 'https://img.insight.co.kr/static/2018/10/17/700/o7nzfv4duvvp6303kht7.jpg')
INSERT INTO `makgeolli` VALUES ('19', 'Ihwaju', '19', 'Ihwaju, which has been handed down since the Goryeo Dynasty, is a precious drink that the nobles enjoyed mainly. It is expensive and has high degree compared to other makgeolli. It is makgeolli that is eaten like yogurt.', 'Korea', 'yogurt', 'noble', 'high', '2.6', 'https://file.mk.co.kr/meet/neds/2020/02/image_readtop_2020_199706_15826968624101861.jpg')
INSERT INTO `makgeolli` VALUES ('20', 'Daedaepo Makgeolli', '20', 'The taste is sweet and soft, the flavor of rice is alive, there is less hangover, and the shelf life is longer than other products.', 'Korea', 'sweet', 'soft', 'alive', '2.4', 'https://thesool.com/wp-content/uploads/2020/05/%EB%8C%80%EB%8C%80%ED%8F%AC-%EB%A7%89%EA%B1%B8%EB%A6%AC.png')